package com.sdzy.day02;import java.util.Scanner;public class Guessing {    public static void  main(String[] args){        Scanner scan = new Scanner(System.in);        System.out.printf("猜数字游戏，请输入（0-999）整数，请输入：");        int num = (int) (Math.random()*1000);        int guess = scan.nextInt();        while (guess!=num) {            if (guess > num) {                System.out.println("猜大了");            } else {                System.out.println("猜小了");            }            guess = scan.nextInt();        }        System.out.println("猜对了");    }}